    def return_vars_as_flat_list(self, keys='all variables'):
        '''this fct will return elements of the entire dictionary.
        keys=... lets you specify certain parts.
        '''
        list_of_all_elements = []
        for key in keys:
            for element in self.var_dictionary[key]:
                list_of_all_elements.append(element)            
        return list_of_all_elements


    def helper_method_to_get_all_dummies(df):
        for dummy in ['dayofweek','nat_name','c_asy_type','year',
                      'citymonth','chair']:
            string = '['
            for col in df.columns:
                if dummy in col:
                    string=string+"'"+col+"',"
            string += ']'
            print(string)
            print("Caveat: this output needs manual corrections")
